package com.siri.ShopingCart.product;

public class Department {
	private int id;
	private String name;
	public Department() {
		super();
		// TODO Auto-generated constructor stub
	}
	
	
	public Department(int id, String name) {
		this.id=id;
		this.name=name;
	}


	public int getId() {
		return id;
	}
	public String getName() {
		return name;
	}
	

}
